<script>
export default {
  data () {
    return {
      mouseX: 0,
      mouseY: 0,
      scrollY: 0,
      coordinateX: 0,
      coordinateY: 0,
      winW: 0,
      winH: 0,
      movingObjects: []
    }
  },
  methods: {
    moveObjects (e) {
      this.mouseX = e.pageX
      this.mouseY = e.pageY
      this.scrollY = window.scrollY
      this.coordinateX = (this.winW / 2) - this.mouseX
      this.coordinateY = (this.winH / 2) - (this.mouseY - this.scrollY)

      for (let i = 0; i < this.movingObjects.length; i++) {
        const translatingFactor = this.movingObjects[i].getAttribute('data-translating-factor') || 20
        const rotatingFactor = this.movingObjects[i].getAttribute('data-rotating-factor') || 20
        const perspective = this.movingObjects[i].getAttribute('data-perspective') || 500
        let tranformProperty = []

        if (this.movingObjects[i].classList.contains('is-translating')) {
          tranformProperty.push('translate(' + this.coordinateX / translatingFactor + 'px, ' + this.coordinateY / translatingFactor + 'px)')
        }

        if (this.movingObjects[i].classList.contains('is-rotating')) {
          tranformProperty.push('perspective(' + perspective + 'px) rotateY(' + -this.coordinateX / rotatingFactor + 'deg) rotateX(' + this.coordinateY / rotatingFactor + 'deg)')
        }

        if (this.movingObjects[i].classList.contains('is-translating') || this.movingObjects[i].classList.contains('is-rotating')) {
          tranformProperty = tranformProperty.join(' ')

          this.movingObjects[i].style.transform = tranformProperty
          this.movingObjects[i].style.transition = 'transform 1s ease-out'
          this.movingObjects[i].style.transformStyle = 'preserve-3d'
          this.movingObjects[i].style.backfaceVisibility = 'hidden'
        }
      }
    }
  },
  mounted () {
    const doc = document.documentElement

    doc.classList.remove('no-js')
    doc.classList.add('js')

    // Reveal animations
    if (document.body.classList.contains('has-animations')) {
      /* global ScrollReveal */
      const sr = window.sr = ScrollReveal()

      sr.reveal('.hero-title, .hero-paragraph, .hero-cta', {
        duration: 1000,
        distance: '40px',
        easing: 'cubic-bezier(0.5, -0.01, 0, 1.005)',
        origin: 'bottom',
        interval: 150
      })
    }

    // Moving objects
    this.movingObjects = document.querySelectorAll('.is-moving-object')

    // Throttling
    function throttle (func, milliseconds) {
      let lastEventTimestamp = null
      let limit = milliseconds

      return (...args) => {
        let now = Date.now()

        if (!lastEventTimestamp || now - lastEventTimestamp >= limit) {
          lastEventTimestamp = now
          func.apply(this, args)
        }
      }
    }

    // Init vars
    this.winW = doc.clientWidth
    this.winH = doc.clientHeight

    // Call function with throttling
    if (this.movingObjects) {
      window.addEventListener('mousemove', throttle(
        this.moveObjects,
        150
      ))
    }
  }
}
